import axios from 'axios';
import Swal from 'sweetalert2';
import { useState } from 'react';

function Preguntas() {

    let [DeportesPasatiempos, setDeportesPasatiempos] = useState([{}]);
    /*const [ArteLiteratura, setArteLiteratura] = useState([]);
    const [Historia, setHistoria] = useState([]);
    const [Geografia, setGeografia] = useState([]);
    const [CienciaNaturaleza, setCienciaNaturaleza] = useState([]);*/

    const obtenerPreguntas = async() => {
        const preguntas = await axios.get('http://localhost:5000/preguntas');

        for(let i = 0; i < preguntas.data.length; i++) {
            if(preguntas.data[i].categoria === 'Deportes/Pasatiempos'){

                const pregunta = {
                    id: preguntas.data[i].id,
                    categoria: preguntas.data[i].categoria,
                }
               
                setDeportesPasatiempos([...DeportesPasatiempos, preguntas.data[i]]);                
            }
        }
        
        console.log(DeportesPasatiempos);
    }

    function play() {
        obtenerPreguntas();
        Swal.fire({
            title: 'Preguntas',
            html: 'I will close in <b></b> milliseconds.',
            timer: 5000,
            timerProgressBar: true,
            
        })
    }

    return(
        <div className='container_index'>
            <button onClick={play}>New game</button>
            <a href='./'>Back</a>
        </div>
    )
};

export default Preguntas;
